formField:
interface FormFieldProps {
  label?: string
  placeholder?: string
  helperText?: string
  id?: string
  value?: string
  onChange?: (e: React.ChangeEvent<HTMLInputElement>) => void
}

function EmailFormField({ 
  label = "Email Address",
  placeholder = "john@example.com",
  helperText = "We'll never share your email with anyone else.",
  id = "email",
  value,
  onChange
}: FormFieldProps) {
  return (
    <div className="space-y-2">
      <Label htmlFor={id}>{label}</Label>
      <div className="relative">
        <Envelope className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
        <Input id={id} type="email" placeholder={placeholder} className="pl-10" value={value} onChange={onChange} />
      </div>
      <p className="text-sm text-muted-foreground">
        {helperText}
      </p>
    </div>
  )
}

searchBar:
interface SearchBarProps {
  placeholder?: string
  value?: string
  onChange?: (e: React.ChangeEvent<HTMLInputElement>) => void
}

function SearchBar({ placeholder = "Search...", value, onChange }: SearchBarProps) {
  return (
    <div className="relative">
      <MagnifyingGlass className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
      <Input placeholder={placeholder} className="pl-10" value={value} onChange={onChange} />
    </div>
  )
}

searchBarWithButton:
interface SearchBarWithButtonProps {
  placeholder?: string
  buttonText?: string
  value?: string
  onChange?: (e: React.ChangeEvent<HTMLInputElement>) => void
  onSearch?: () => void
}

function SearchBarWithButton({ 
  placeholder = "Search...",
  buttonText = "Search",
  value,
  onChange,
  onSearch
}: SearchBarWithButtonProps) {
  return (
    <div className="flex gap-2">
      <div className="relative flex-1">
        <MagnifyingGlass className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
        <Input placeholder={placeholder} className="pl-10" value={value} onChange={onChange} />
      </div>
      <Button onClick={onSearch}>{buttonText}</Button>
    </div>
  )
}

socialActions:
interface SocialActionsProps {
  likeText?: string
  commentText?: string
  shareText?: string
  onLike?: () => void
  onComment?: () => void
  onShare?: () => void
}

function SocialActions({ 
  likeText = "Like",
  commentText = "Comment",
  shareText = "Share",
  onLike,
  onComment,
  onShare
}: SocialActionsProps) {
  return (
    <div className="flex items-center gap-2">
      <Button variant="ghost" size="sm" onClick={onLike}>
        <Heart className="mr-2" />
        {likeText}
      </Button>
      <Button variant="ghost" size="sm" onClick={onComment}>
        <ChatCircle className="mr-2" />
        {commentText}
      </Button>
      <Button variant="ghost" size="sm" onClick={onShare}>
        <Share className="mr-2" />
        {shareText}
      </Button>
    </div>
  )
}

statusIndicator:
interface StatusIndicatorProps {
  statusText?: string
  badgeText?: string
  isActive?: boolean
}

function StatusIndicator({ 
  statusText = "System Online",
  badgeText = "Active",
  isActive = true
}: StatusIndicatorProps) {
  return (
    <div className="flex items-center justify-between">
      <div className="flex items-center gap-3">
        <div className={cn(
          "h-3 w-3 rounded-full bg-accent",
          isActive && "animate-pulse"
        )} />
        <span className="font-medium">{statusText}</span>
      </div>
      <Badge>{badgeText}</Badge>
    </div>
  )
}
