# Find the sum of all multiples of 3 or 5 below 1000

def solve_euler_1(limit=1000):
    total = 0
    for i in range(limit):
        if i % 3 == 0 or i % 5 == 0:
            total += i
    return total

result = solve_euler_1(1000)
print(f"The sum of all multiples of 3 or 5 below 1000 is: {result}")

# More efficient solution using arithmetic series
def solve_euler_1_optimized(limit=1000):
    def sum_divisible_by(n):
        p = (limit - 1) // n
        return n * p * (p + 1) // 2

    return sum_divisible_by(3) + sum_divisible_by(5) - sum_divisible_by(15)

result_optimized = solve_euler_1_optimized(1000)
print(f"Optimized solution: {result_optimized}")

